INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_CSTATE_CULTURE_BONUS', 'Estás recibiendo [COLOR_POSITIVE_TEXT]cultura[ICON_CULTURE][ENDCOLOR] de ellos en todas tus ciudades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_CSTATE_FOOD_BONUS', 'Estás recibiendo [COLOR_POSITIVE_TEXT]alimentos[ICON_FOOD][ENDCOLOR] de ellos en todas tus ciudades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_CSTATE_MILITARY_BONUS', 'Te enviá [COLOR_POSITIVE_TEXT]nuevas unidades militares[ENDCOLOR] cada varios turnos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_BASE', 'Tu influencia [ICON_INFLUENCE] sobre {1_CivName:textkey} ha crecido has el punto de que ahora sois [COLOR_POSITIVE_TEXT]AMIGOS[ENDCOLOR].[NEWLINE][NEWLINE]{2_DetailsString} También te han dado permiso para mover las unidades a través de su territorio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_CULTURE', 'Como ciudad-estado [COLOR_POSITIVE_TEXT]cultural[ENDCOLOR], te dan raras obras de arte, lo que aumenta la cultura [ICON_CULTURE] de tus ciudades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_MILITARISTIC', 'Como ciudad-estado [COLOR_POSITIVE_TEXT]militarista[ENDCOLOR], te darán ocasionalmente unidades militares.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_MARITIME', 'Como ciudad-estado [COLOR_POSITIVE_TEXT]marítima[ENDCOLOR], te exportaran alimentos [ICON_FOOD] para tus ciudades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_MERCANTILE', 'Como ciudad-estado [COLOR_POSITIVE_TEXT]mercantil[ENDCOLOR], sus mercados ofrecen exóticos bienes que proporcionan felicidad [ICON_HAPPINESS_1] entre tu gente', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-24'), 'TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_RELIGIOUS', 'Como ciudad-estado [COLOR_POSITIVE_TEXT]religiosa[ENDCOLOR], compartirán su conocimiento de los rituales y sus reliquias contigo, lo que proporciona Fe [ICON_PEACE]', '', '');
