INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-06'), 'TXT_KEY_MISC_ENEMY_UNIT_WITHDRAW', '¡Un {@1_EnUName} enemigo ([COLOR_POSITIVE_TEXT]{2_Num}[ENDCOLOR] daño) atacó tu {@3_UnitName} ([COLOR_NEGATIVE_TEXT]{4_Num}[ENDCOLOR] daño)!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-06'), 'TXT_KEY_MISC_ENEMY_UNIT_WITHDRAW_CITY', '¡Un {@1_EnUName} enemigo ([COLOR_POSITIVE_TEXT]{2_Num}[ENDCOLOR] daño) atacó tu ciudad de {@3_CityName} ([COLOR_NEGATIVE_TEXT]{4_Num}[ENDCOLOR] daño)!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-06'), 'TXT_KEY_MISC_YOU_AIR_UNIT_HURT', '¡Tu {@1_UnitName} fue [COLOR_NEGATIVE_TEXT]interceptada[ENDCOLOR] por un {@2_EnUName} enemigo! ({3}% daño)', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-06'), 'TXT_KEY_MISC_YOU_ARE_ATTACKED_BY_AIR', '¡Tu {@1_UnitName} ([COLOR_NEGATIVE_TEXT]{3_Num}[ENDCOLOR] daño) fue bombardeada por un {@2_EnUName} enemigo!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-06'), 'TXT_KEY_MISC_YOU_ARE_ATTACKED_BY_AIR_AND_DEATH', '¡Tu {@1_UnitName} fue [COLOR_NEGATIVE_TEXT]destruido [ENDCOLOR] despues de haber sido bombardeada por un {@2_EnUName} enemigo!', '', '');
